import React, { useEffect, useState } from 'react';
import { Card, InputGroup } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Form  from 'react-bootstrap/Form';
import { useParams } from 'react-router-dom';

import "../style/Task.css" ;

import { 
    setDoc, 
    doc, 
    getDoc,
    Timestamp 
} from 'firebase/firestore';
import db from "../database/firebase"

import { 
    Label, 
    AreaChart, 
    Area, 
    CartesianGrid, 
    XAxis, 
    YAxis, 
    Tooltip 
} from 'recharts';
import ReactExport from "react-data-export";

const ExcelFile = ReactExport.ExcelFile;
const ExcelSheet = ReactExport.ExcelFile.ExcelSheet;
const ExcelColumn = ReactExport.ExcelFile.ExcelColumn;


const Task = () => {

    const { projectId } = useParams();

    /*  taskArray = [
            { "id": index, "data": { place:..., description:..., ... }}, 
            { "id": index, "data": { place:..., description:..., ... }}, 
            { "id": index, "data": { place:..., description:..., ... }}, 
            ...
        ]
    */
    const [taskArray, setTaskArray] = useState([])

    /*  projectData = [
            projectName: "Project 101",
            description: "",
            name: "",
            date: "",
            measureType: "", 
            tasksData: []
        ]
    */
    const [projectData, setProjectData] = useState([])

    useEffect(() => {
        getTasksData()
    }, [])  

    const getTasksData = async () => {
        const projData = await getDoc(doc(db, "Project", projectId))
        const dt = projData.data()
        setProjectData(dt)
    }

    const updateScaParameterToDatabase = async () => {
        await setDoc(doc(db, "ScaParameter", "u11HXFCRjpZQ1STD75iy"), {
            status: scaParameter.status,
            LLD: scaParameter.LLD,
            window: scaParameter.window,
            time: scaParameter.time,
            id: scaParameter.id
        })
    }


    // Fetch All Tasks to Card ----------------------------------------------

    const renderDate = (date, index) => {
        const dateObj = new Date(date.seconds * 1000)
        const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`
        return current
    }


    // Send Sca Parameter (LLD, Window, Time) to Backend ----------------------------------------------


    const [scaParameter, setScaParameter] = useState({
        status: false,
        LLD: 0,
        window: 10,
        time: 5,
        id: ""
    })

    function onSumitParameterChange(event) {
        const { name, value } = event.target
        setScaParameter((prevData) => {
            return {
                ...prevData,
                [name]: value
            }
        })
    }

    const formSubmitSca = async (event) => {
        event.preventDefault()
    }

    const doStartCounting = (tId) => {
        scaParameter["status"] = true
        scaParameter["id"] = tId
        updateScaParameterToDatabase()
        console.log(scaParameter)
    }


    // Display Graph ----------------------------------------------

    const renderCountsData = (dataValue) => {

        return(
            <>
                <div className='chart-display'>
                    <div className='chart-chart'>
                        <AreaChart width={800} height={400} data={dataValue} margin={{ top: 75, right: 75, left: 75, bottom: 50}}>
                            <Area type="monotone" dataKey="counts" stroke="#8884d8" fill="#8884d8" />
                            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
                            <XAxis dataKey="energy">
                                <Label value="Energy" offset={10} position="bottom" />
                            </XAxis>
                            <YAxis>
                                <Label value="Counts" offset={10} position="left" angle={-90} textAnchor="start" />
                            </YAxis>
                            <Tooltip />
                        </AreaChart>
                    </div>

                    <div className='chart-button'>
                        <ExcelFile filename={projectData.projectName + " MCA Spectrum"} element={<button className='download-chart'>Download Data</button>}>
                            <ExcelSheet data={dataValue} name="MCA Spectrum">
                                <ExcelColumn label="Energy" value="energy"/>
                                <ExcelColumn label="Counts" value="counts"/>
                            </ExcelSheet>
                        </ExcelFile>
                    </div>
                </div>
            </>
        )
    }





    return (
        <>

        <div className='Task'> 

            <div className='mca-display'>

                <div className='col-md-8'>
                    <Card>
                        <div className='operation'>
                            <Card.Body>
                                <Card.Title>{projectData.projectName}</Card.Title>
                                <Card.Text>{renderDate}</Card.Text>
                                <Card.Text>{projectData.name}</Card.Text>
                                <Card.Text>{projectData.measureType}</Card.Text>
                            </Card.Body>
                            <div className='mca-button'>
                                <Button variant="warning" className="m-1" onClick={() => {
                                    // Edit Function
                                }}>
                                    <i className="fa-regular fa-pen-to-square"></i>
                                </Button>
                            </div>
                        </div>
                        <Card.Body className='inputZone'>
                            <Form onSubmit={formSubmitSca} id="send-LLD-Window-Time">

                                <InputGroup className="mb-3">
                                    <InputGroup.Text id="basic-addon1">LLD</InputGroup.Text>
                                    <Form.Control
                                        name="LLD" 
                                        placeholder="LLD (0 - 10 V)" 
                                        onChange={onSumitParameterChange}
                                    />
                                    <InputGroup.Text id="basic-addon1">Window</InputGroup.Text>
                                    <Form.Control
                                        name="window" 
                                        placeholder="Window (0 - 10 V)" 
                                        onChange={onSumitParameterChange}
                                    />
                                    <InputGroup.Text id="basic-addon1">Time</InputGroup.Text>
                                    <Form.Control
                                        name="time" 
                                        placeholder="Time/Step (0 - 60 sec)" 
                                        onChange={onSumitParameterChange}
                                    />
                                </InputGroup>
                            </Form>


                            <Button variant="primary" className="m-1" onClick={() => {       
                                doStartCounting(projectId)
                            }} type="submit" form="send-LLD-Window-Time" value="Submit">
                                {"Start Counting"}
                            </Button>
                        </Card.Body>
                        <Card.Footer className='graphZone'>
                            {renderCountsData(projectData.tasksData)}
                        </Card.Footer>

                    </Card>

                </div>  
            </div>

            <div className='footer'>
                <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                        <p><i className="fa-solid fa-plus"></i></p>
                </Button>
            </div>  
        </div>









        </>
    )
}

export default Task;
