    const [createShow, setCreateShow] = useState(false);
    const handleCreateClose = () => setCreateShow(false);
    const handleCreateShow = () => setCreateShow(true);

    return (
        <>
        <Navbar/>
        <div className='Task'>
            <div className='row container'>
                <div className='col-12'>
                    <Card style={{}}>
                        <Card.Body>
                            <Card.Title>{projectData.projectName}</Card.Title>
                            <Card.Text>Name</Card.Text>
                            <Card.Text>Date</Card.Text>
                            <Card.Text>Type</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            </div>

            {taskArray.map(renderTaskData)}

            <div className='footer'>
                    <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                            <p><i class="fa-solid fa-plus"></i></p>
                    </Button>
            </div>  
        </div>









        <Modal show={createShow} onHide={handleCreateClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Create Project</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={formSubmit} id="create-project">

                        <Form.Group className="mb-3" controlId="formProjectName">
                            <Form.Label>Project Name</Form.Label>
                            <Form.Control 
                                name="projectName" 
                                placeholder="project name" 
                                onChange={onCreateDataChange}
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control 
                                as="textarea" rows={3}
                                name="description" 
                                placeholder="description" 
                                onChange={onCreateDataChange}
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control 
                                name="name" 
                                placeholder="name" 
                                onChange={onCreateDataChange}
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formMeasureType">
                            <Form.Label>Measurement Type</Form.Label>
                            <Form.Select    
                                name="measureType" 
                                onChange={onCreateDataChange}
                                aria-label="Default select example"
                            >
                                <option>Counter/Timer</option>
                                <option>Ratemeter</option>
                                <option>MCA</option>
                            </Form.Select>
                        </Form.Group>

                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleCreateClose} type="submit" form="create-project" value="Submit">Create Project</Button>     
                </Modal.Footer>
            </Modal>





        </>
    )
}

export default Task;
