import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import "../style/Task.css" ;
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


import { addDoc, 
    setDoc, 
    collection, 
    onSnapshot, 
    query, 
    orderBy, 
    doc, 
    deleteDoc,
    getDoc,
    Timestamp } from 'firebase/firestore';
import db from "../database/firebase"




const Task = () => {

    const { projectId } = useParams();

    const [taskArray, setTaskArray] = useState([])
    const [projectData, setProjectData] = useState([])

    useEffect(() => {
        onSnapshot(doc(db, "Project", projectId), (snapshot) => {
            const dt = snapshot.data()

            console.log(dt)
            setProjectData(dt)
            console.log(dt.data)
            setTaskArray(dt.data)
        })
    }, [])  

    // const dateObj = new Date(data.date.seconds * 1000)
    // const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`


    // Database to Card ----------------------------------------------
    const renderTaskData = (task, index) => {
        return
    }



    // 

    const [createShow, setCreateShow] = useState(false);
    const handleCreateClose = () => setCreateShow(false);
    const handleCreateShow = () => setCreateShow(true);

    return (
        <>
        <Navbar/>
        <div className='Task'>
            <div className='row container'>
                <div className='col-12'>
                    <Card style={{}}>
                        <Card.Body>
                            <Card.Title>{projectData.projectName}</Card.Title>
                            <Card.Text>Name</Card.Text>
                            <Card.Text>Date</Card.Text>
                            <Card.Text>Type</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            </div>

            {taskArray.map(renderTaskData)}

            <div className='footer'>
                    <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                            <p><i class="fa-solid fa-plus"></i></p>
                    </Button>
            </div>  
        </div>









        <Modal show={editShow} onHide={handleEditClose}>
            <Modal.Header closeButton>
                <Modal.Title>Edit Project</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={editSubmit} id="edit-project">

                    <Form.Group className="mb-3" controlId="formProjectName">
                        <Form.Label>Project Name</Form.Label>
                        <Form.Control 
                            name="projectName" 
                            value={editProjectName}
                            placeholder="edit project name" 
                            onChange={onEditProjectNameChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formDescription">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" rows={3}
                            name="description" 
                            value={editDescription}
                            placeholder="edit description" 
                            onChange={onEditDescriptionChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control 
                            name="name" 
                            value={editName}
                            placeholder="edit name"
                            onChange={onEditNameChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formMeasureType">
                        <Form.Label>Measurement Type</Form.Label>
                        <Form.Select    
                            name="measureType" 
                            disabled
                        >
                            <option>{editMeasureType}</option>
                        </Form.Select>
                    </Form.Group>


                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="light" onClick={handleEditClose}>
                    Cancel
                </Button> 
                <Button variant="warning" onClick={handleEditClose} type="submit" form="edit-project" value="Submit">Edit Project</Button>     
            </Modal.Footer>
        </Modal>





        </>
    )
}

export default Task;
