import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import { useParams } from 'react-router-dom';
import uuid from 'react-uuid';

import "../style/Task.css" ;

import { 
    setDoc, 
    doc, 
    getDoc,
    Timestamp 
} from 'firebase/firestore';
import db from "../database/firebase"

import { 
    Label, 
    Line, 
    AreaChart, 
    Area, 
    CartesianGrid, 
    XAxis, 
    YAxis, 
    Tooltip 
} from 'recharts';
import ReactExport from "react-data-export";

const ExcelFile = ReactExport.ExcelFile;
const ExcelSheet = ReactExport.ExcelFile.ExcelSheet;
const ExcelColumn = ReactExport.ExcelFile.ExcelColumn;


const Task = () => {

    const { projectId } = useParams();

    /*  taskArray = [
            { "id": index, "data": { place:..., description:..., ... }}, 
            { "id": index, "data": { place:..., description:..., ... }}, 
            { "id": index, "data": { place:..., description:..., ... }}, 
            ...
        ]
    */
    const [taskArray, setTaskArray] = useState([])

    /*  projectData = [
            projectName: "Project 101",
            description: "",
            name: "",
            date: "",
            measureType: "", 
            tasksData: []
        ]
    */
    const [projectData, setProjectData] = useState([])

    useEffect(() => {
        getData()
    }, [])  

    const getData = async () => {
        const projData = await getDoc(doc(db, "Project", projectId))
        const dt = projData.data()
        setProjectData(dt)
        setTaskArray(dt.tasksData)
    }

    const updateDataToDatabase = async () => {
        await setDoc(doc(db, "Project", projectId), {
            projectName: projectData.projectName,
            description: projectData.description,
            name: projectData.name,
            date: projectData.date,
            measureType: projectData.measureType, 
            tasksData: taskArray
        })
    }


    // Database to Card ----------------------------------------------

    const renderTaskData = (task, index) => {
        // task is {"id": ..., "data": { place:..., description:..., ... }}
        const dateObj = new Date(task.data.date.seconds * 1000)
        const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`

        return(
            <div className='mca-display'>

                    <div className='col-md-8'>
                        <Card>
                            <div className='operation'>
                                <Card.Body>
                                    <Card.Title>{task.data.place}</Card.Title>
                                    <Card.Text>{current}</Card.Text>
                                    <Card.Text>{task.data.description}</Card.Text>
                                    <Card.Text>{task.id}</Card.Text>
                                </Card.Body>
                                <div className='mca-button'>
                                    <Button variant="warning" className="m-1" onClick={() => {
                                        setEditTaskId(task.id)
                                        setEditTaskPlace(task.data.place)
                                        setEditTaskDescription(task.data.description)
                                        setEditTaskDate(task.data.date)
                                        setEditTaskData(task.data.taskData)
                                        handleEditShow()
                                    }}>
                                        <i className="fa-regular fa-pen-to-square"></i>
                                    </Button>
                                    <Button variant="danger" className="m-1" onClick={() => {       
                                        setDeleteTaskId(task.id)
                                        handleAlertShow()
                                    }}>
                                        <i className="fa-regular fa-trash-can"></i>
                                    </Button>
                                </div>
                            </div>
                            <Card.Body>
                                <Button variant="primary" className="m-1" onClick={() => {       
                                    doStartCounting(task.id)
                                }}>
                                    {"Start Counting"}
                                </Button>
                            </Card.Body>
                            <Card.Body>
                                {renderCountsData(task.data.taskData)}
                            </Card.Body>

                        </Card>

                </div>  
            </div>
        )
    }


    // Type Operation ----------------------------------------------

    const renderCountsData = (data) => {

        console.log()

        const dataSet1 = [
            { energy: 10, counts: 20 },
            { energy: 100,counts: 10000 },
            { energy: 500, counts: 400 },
            { energy: 1000, counts: 800 },
            { energy: 5000, counts: 2000 },
            { energy: 10000, counts: 30 },
            { energy: 50000, counts: 12000 },
            { energy: 100000, counts: 3000 }
        ]

        return(
            <>
                <div className='chart-display'>
                    <div className='chart-chart'>
                        <AreaChart width={800} height={400} data={dataSet1} margin={{ top: 75, right: 75, left: 75, bottom: 50}}>
                            <Area type="monotone" dataKey="counts" stroke="#8884d8" fill="#8884d8" />
                            <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
                            <XAxis dataKey="energy">
                                <Label value="Energy" offset={10} position="bottom" />
                            </XAxis>
                            <YAxis>
                                <Label value="Counts" offset={10} position="left" angle={-90} textAnchor="start" />
                            </YAxis>
                            <Tooltip />
                        </AreaChart>
                    </div>

                    <div className='chart-button'>
                        <ExcelFile filename={projectData.projectName + " MCA Spectrum"} element={<button className='download-chart'>Download Data</button>}>
                            <ExcelSheet data={dataSet1} name="MCA Spectrum">
                                <ExcelColumn label="Energy" value="energy"/>
                                <ExcelColumn label="Counts" value="counts"/>
                            </ExcelSheet>
                        </ExcelFile>
                    </div>
                </div>
            </>
        )
    }

    const doStartCounting = (id) => {
        let taskIndex 
        taskArray.forEach((task) => {
            if(task.id === id) {  
                taskIndex = taskArray.indexOf(task)
            }    
        })

        // communication with database to fetch data from ESP32
        taskArray[taskIndex].data.taskData.push("test")


        updateDataToDatabase()
        getData()
    }



    // Add Task to Database ----------------------------------------------

    const [createShow, setCreateShow] = useState(false);
    const handleCreateClose = () => setCreateShow(false);
    const handleCreateShow = () => setCreateShow(true);

    const [data, setData] = useState({
        place: "Unknown Place",
        description: "This task does not have description",
        date: "",
        taskData: []
    })

    function onCreateDataChange(event) {
        const { name, value } = event.target
        setData((prevData) => {
            return {
                ...prevData,
                [name]: value
            }
        })
    }

    const formSubmit = async (event) => {
        event.preventDefault()

        const timeStamp = Timestamp.fromDate(new Date());
        setData((prevData) => {
            prevData["date"] = timeStamp
            return prevData
        })

        const tId = uuid()
        taskArray.push({"id": tId, "data": data})

        updateDataToDatabase()
        getData()
        console.log("Creating Task: id = ", tId)
        setData({
            place: "Unknown Place",
            description: "This task does not have description",
            date: "",
            taskData: [
                { energy: 10, counts: 20 },
                { energy: 100,counts: 10000 },
                { energy: 500, counts: 400 },
                { energy: 1000, counts: 800 },
                { energy: 5000, counts: 2000 },
                { energy: 10000, counts: 30 },
                { energy: 50000, counts: 12000 },
                { energy: 100000, counts: 3000 }
            ]
        })
    }

    // Edit Task ----------------------------------------------

    const [editShow, setEditShow] = useState(false)
    const handleEditClose = () => setEditShow(false)
    const handleEditShow = () => setEditShow(true)

    const [editTaskId, setEditTaskId] = useState("")
    const [editTaskPlace, setEditTaskPlace] = useState("") 
    const [editTaskDescription, setEditTaskDescription] = useState("")
    const [editTaskDate, setEditTaskDate] = useState("")
    const [editTaskData, setEditTaskData] = useState([])

    const onEditTaskPlaceChange = (event) => {
        event.preventDefault() // prevent the default action
        setEditTaskPlace(event.target.value)
    }

    const onEditTaskDescriptionChange = (event) => {
        event.preventDefault() 
        setEditTaskDescription(event.target.value);
    }

    const editSubmit = async (event) => {
        event.preventDefault()

        let taskIndex 
        taskArray.forEach((task) => {
            if(task.id === editTaskId) {  
                taskIndex = taskArray.indexOf(task)
            }    
        })

        taskArray.splice(taskIndex, 1, {
            id: editTaskId,
            data: {
                place: editTaskPlace,
                description: editTaskDescription,
                date: editTaskDate,
                taskData: editTaskData
            }
        })

        updateDataToDatabase()
        getData()
        console.log("Editing Task: id = ", editTaskId)
    }

    // Delete Task ----------------------------------------------

    const [alertShow, setAlertShow] = useState(false);
    const handleAlertClose = () => setAlertShow(false);
    const handleAlertShow = () => setAlertShow(true);

    const [deleteTaskId, setDeleteTaskId] = useState("")


    const deleteTask = () => {
        let taskIndex 
        taskArray.forEach((task) => {
            if(task.id === deleteTaskId) {  
                taskIndex = taskArray.indexOf(task)
            }    
        })

        taskArray.splice(taskIndex, 1)
        handleAlertClose()
        updateDataToDatabase()
        getData()
        console.log("Deleting Task: id = ", deleteTaskId)
    }





    return (
        <>

        <section className="main-content">
            <br></br>
            <br></br>
            <div className='head'>
                <h1>
                    <u>{projectData.projectName}</u>
                </h1>
                <h2>
                    {projectData.name}
                </h2>
                <h2>
                    {projectData.measureType}
                </h2>
            </div>
            <br></br>
            <br></br>
        </section>   



        <div className='Task'> 

            {taskArray.map(renderTaskData)}

            <div className='footer'>
                <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                        <p><i className="fa-solid fa-plus"></i></p>
                </Button>
            </div>  
        </div>









        <Modal show={createShow} onHide={handleCreateClose}>
            <Modal.Header closeButton>
                <Modal.Title>Create Task</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={formSubmit} id="create-task">

                    <Form.Group className="mb-3" controlId="formPlace">
                        <Form.Label>Place</Form.Label>
                        <Form.Control 
                            name="place" 
                            placeholder="place" 
                            onChange={onCreateDataChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formDescription">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" rows={3}
                            name="description" 
                            placeholder="description" 
                            onChange={onCreateDataChange}
                        />
                    </Form.Group>

                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="primary" onClick={handleCreateClose} type="submit" form="create-task" value="Submit">Create Task</Button>     
            </Modal.Footer>
        </Modal>















        <Modal show={alertShow} onHide={handleAlertClose}>
            <Modal.Header>
                <Modal.Title>Deleting Task ?</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <p>
                When clicking on the "Delete" button, 
                the system will remove all task data from this project permanently. 
                It means that data in task cannot be restored.
            </p>    
            <p>
                Press "Cancel" button to cancel.
            </p>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="light" onClick={handleAlertClose}>
                    Cancel
                </Button> 
                <Button variant="danger" onClick={deleteTask}>
                    Delete
                </Button>     
            </Modal.Footer>
        </Modal>











        <Modal show={editShow} onHide={handleEditClose}>
            <Modal.Header closeButton>
                <Modal.Title>Edit Task</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={editSubmit} id="edit-task">

                    <Form.Group className="mb-3" controlId="formPlace">
                        <Form.Label>Place</Form.Label>
                        <Form.Control 
                            name="place" 
                            value={editTaskPlace}
                            placeholder="edit place" 
                            onChange={onEditTaskPlaceChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formDescription">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" rows={3}
                            name="description" 
                            value={editTaskDescription}
                            placeholder="edit description" 
                            onChange={onEditTaskDescriptionChange}
                        />
                    </Form.Group>

                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="light" onClick={handleEditClose}>
                    Cancel
                </Button> 
                <Button variant="warning" onClick={handleEditClose} type="submit" form="edit-task" value="Submit">Edit Task</Button>     
            </Modal.Footer>
        </Modal>







        </>
    )
}

export default Task;
