import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import "../style/Task.css" ;

import { addDoc, 
    setDoc, 
    collection, 
    onSnapshot, 
    query, 
    orderBy, 
    doc, 
    deleteDoc,
    getDoc,
    Timestamp } from 'firebase/firestore';
import db from "../database/firebase"




const Task = () => {

    const { projectId } = useParams();

    /* taskArray is [{"id": index, "data": { place:..., description:..., ... }}, 
                    {"id": index, "data": { place:..., description:..., ... }}, 
                    {"id": index, "data": { place:..., description:..., ... }}, ...]
    */
    const [taskArray, setTaskArray] = useState([])

    /* projectData is [projectName: "Project 101",
                        description: "",
                        name: "",
                        date: "",
                        measureType: "", 
                        tasksData: []]
    */
    const [projectData, setProjectData] = useState([])

    useEffect(() => {
        const getData = async () => {
            const projData = await getDoc(doc(db, "Project", projectId))
            const dt = projData.data()
            setProjectData(dt)
            setTaskArray(dt.tasksData)
        }
        getData()
    }, [])  

    const updateDataToDatabase = async () => {
        await setDoc(doc(db, "Project", projectId), {
            projectName: projectData.projectName,
            description: projectData.description,
            name: projectData.name,
            date: projectData.date,
            measureType: projectData.measureType, 
            tasksData: taskArray
        })
    }


    // Database to Card ----------------------------------------------

    const renderTaskData = (task, index) => {
        // task is {"id": ..., "data": { place:..., description:..., ... }}
        const dateObj = new Date(task.data.date.seconds * 1000)
        const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`
        return(
            <div className='mca-display'>
                <div className='row container'>
                    <div className='col-12'>
                        <Card>
                            <Card.Body>
                                <Card.Title>{task.data.place}</Card.Title>
                                <Card.Text>{task.data.description}</Card.Text>
                                <Card.Text>{current}</Card.Text>
                                <Card.Text>{task.id}</Card.Text>
                            </Card.Body>
                            <Button variant="warning" className="m-1" onClick={() => {
                                setEditTaskId(task.id)
                                setEditTaskPlace(task.data.place)
                                setEditTaskDescription(task.data.description)
                                handleEditShow()
                            }}>
                                <i className="fa-regular fa-pen-to-square"></i>
                            </Button>
                            <Button variant="danger" className="m-1" onClick={() => {       
                                setDeleteTaskId(task.id)
                                handleAlertShow()
                            }}>
                                <i className="fa-regular fa-trash-can"></i>
                            </Button>
                            <Button variant="primary" className="m-1" onClick={() => {       
                                doTypeOperation(projectData.measureType)
                            }}>
                                {"Start Counting"}
                            </Button>
                        </Card>
                    </div>
                </div>  
            </div>
        )
    }


    // Type Operation ----------------------------------------------

    const doTypeOperation = (measureType) => {
        if(measureType==="Timer/Counter") {
            console.log("Timer/Counter")
        } else if(measureType==="Ratemeter") {
            console.log("Ratemeter")
        } else if(measureType==="MCA") {
            console.log("MCA")
        } else {
            console.log("Undefined Type")
        }
    }



    // Add Task to Database ----------------------------------------------

    const [createShow, setCreateShow] = useState(false);
    const handleCreateClose = () => setCreateShow(false);
    const handleCreateShow = () => setCreateShow(true);

    const [data, setData] = useState({
        place: "Unknown Place",
        description: "This project does not have description",
        date: "",
        taskData: []
    })

    function onCreateDataChange(event) {
        const { name, value } = event.target
        setData((prevData) => {
            return {
                ...prevData,
                [name]: value
            }
        })
    }


    const [utid, setUtid] = useState(1)

    const formSubmit = async (event) => {
        event.preventDefault()

        const timeStamp = Timestamp.fromDate(new Date());
        setData((prevData) => {
            prevData["date"] = timeStamp
            return prevData
        })

        taskArray.push({"id": utid, "data": data})
        setUtid((prevData) => {
            return prevData + 1
        })

        updateDataToDatabase()
        console.log("Creating Task: id = ", utid)
        setData({
            place: "Unknown Place",
            description: "This task does not have description",
            date: "",
            taskData: []
        })
    }

    // Edit Task ----------------------------------------------

    const [editShow, setEditShow] = useState(false)
    const handleEditClose = () => setEditShow(false)
    const handleEditShow = () => setEditShow(true)

    const [editTaskId, setEditTaskId] = useState("")
    const [editTaskPlace, setEditTaskPlace] = useState("") 
    const [editTaskDescription, setEditTaskDescription] = useState("")

    const onEditTaskPlaceChange = (event) => {
        event.preventDefault() // prevent the default action
        setEditTaskPlace(event.target.value)
    }

    const onEditTaskDescriptionChange = (event) => {
        event.preventDefault() 
        setEditTaskDescription(event.target.value);
    }

    const editSubmit = async (event) => {
        event.preventDefault()

        const selectTask = taskArray.map((task) => {
            if(task.id === editTaskId) {
                const editTaskIndex = taskArray.indexOf(task)
                return {index: editTaskIndex, targetTask: task}
            }
        })

        console.log(selectTask.index)
        // taskArray.splice(selectTask.index, 0, {
        //     taskId: selectTask.targetTask.id,
        //     taskData: {
        //         place: editTaskPlace,
        //         description: editTaskDescription,
        //         date: selectTask.targetTask.data.date,
        //         taskData: selectTask.targetTask.data.taskData
        //     }
        // })

        updateDataToDatabase()
        console.log("Deleting Task: id = ", editTaskId)

    }

    // Delete Task ----------------------------------------------

    const [alertShow, setAlertShow] = useState(false);
    const handleAlertClose = () => setAlertShow(false);
    const handleAlertShow = () => setAlertShow(true);

    const [deleteTaskId, setDeleteTaskId] = useState("")

    const deleteTask = () => {
       const deleteTaskIndex = taskArray.map((task) => {
           if(task.id === deleteTaskId) {
               return taskArray.indexOf(task)
           }
       })
       taskArray.splice(deleteTaskIndex, 1)
       handleAlertClose()
       updateDataToDatabase()
       console.log("Deleting Task: id = ", deleteTaskId)
    }





    return (
        <>
        <Navbar/>
        <div className='Task'>
            <div className='row container'>
                <div className='col-12'>
                    <Card>
                        <Card.Body>
                            <Card.Title>{projectData.projectName}</Card.Title>
                            <Card.Text>{projectData.name}</Card.Text>
                            <Card.Text>{"Time"}</Card.Text>
                            <Card.Text>{projectData.measureType}</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            </div>

            {taskArray.map(renderTaskData)}

            <div className='footer'>
                <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                        <p><i class="fa-solid fa-plus"></i></p>
                </Button>
            </div>  
        </div>









        <Modal show={createShow} onHide={handleCreateClose}>
            <Modal.Header closeButton>
                <Modal.Title>Create Task</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={formSubmit} id="create-task">

                    <Form.Group className="mb-3" controlId="formPlace">
                        <Form.Label>Place</Form.Label>
                        <Form.Control 
                            name="place" 
                            placeholder="place" 
                            onChange={onCreateDataChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formDescription">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" rows={3}
                            name="description" 
                            placeholder="description" 
                            onChange={onCreateDataChange}
                        />
                    </Form.Group>

                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="primary" onClick={handleCreateClose} type="submit" form="create-task" value="Submit">Create Task</Button>     
            </Modal.Footer>
        </Modal>















        <Modal show={alertShow} onHide={handleAlertClose}>
            <Modal.Header>
                <Modal.Title>Deleting Task ?</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <p>
                When clicking on the "Delete" button, 
                the system will remove all task data from this project permanently. 
                It means that data in task cannot be restored.
            </p>    
            <p>
                Press "Cancel" button to cancel.
            </p>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="light" onClick={handleAlertClose}>
                    Cancel
                </Button> 
                <Button variant="danger" onClick={deleteTask}>
                    Delete
                </Button>     
            </Modal.Footer>
        </Modal>











        <Modal show={editShow} onHide={handleEditClose}>
            <Modal.Header closeButton>
                <Modal.Title>Edit Task</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form onSubmit={editSubmit} id="edit-task">

                    <Form.Group className="mb-3" controlId="formPlace">
                        <Form.Label>Place</Form.Label>
                        <Form.Control 
                            name="projectName" 
                            value={editTaskPlace}
                            placeholder="edit place" 
                            onChange={onEditTaskPlaceChange}
                        />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formDescription">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" rows={3}
                            name="description" 
                            value={editTaskDescription}
                            placeholder="edit description" 
                            onChange={onEditTaskDescriptionChange}
                        />
                    </Form.Group>

                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="light" onClick={handleEditClose}>
                    Cancel
                </Button> 
                <Button variant="warning" onClick={handleEditClose} type="submit" form="edit-task" value="Submit">Edit Task</Button>     
            </Modal.Footer>
        </Modal>







        </>
    )
}

export default Task;
