import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import "../style/Task.css" ;
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


import { addDoc, 
    setDoc, 
    collection, 
    onSnapshot, 
    query, 
    orderBy, 
    doc, 
    deleteDoc,
    getDoc,
    Timestamp } from 'firebase/firestore';
import db from "../database/firebase"




const Task = () => {

    const { projectId } = useParams();

    const [databaseArray, setDatabaseArray] = useState([])

    useEffect(() => {
        const data = onSnapshot(doc(db, "Project", projectId), (doc) => {
            console.log("Current data: ", doc.data());
        });
        setDatabaseArray(data)
    }, [])  

    // const dateObj = new Date(data.date.seconds * 1000)
    // const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`


    const renderRadData = (card, index) => {
        return
    }

    const [alertShow, setAlertShow] = useState(false);
    const handleAlertClose = () => setAlertShow(false);
    const handleAlertShow = () => setAlertShow(true);

    return (
        <>
        <Navbar/>
        <div className='Task'>
            <div className='row container'>
                <div className='col-12'>
                    <Card style={{}}>
                        <Card.Body>
                            <Card.Title>Task</Card.Title>
                            <Card.Text>Place</Card.Text>
                            <Card.Text>Name</Card.Text>
                            <Card.Text>Date</Card.Text>
                            <Card.Text>Type</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            </div>
            <div className='footer'>
                        <div>
                        <Button variant="primary" className="NewTaskButton-2" onClick={handleAlertShow}>
                                    <p><i class="fa-solid fa-plus"></i></p>
                                </Button>
                        </div>
            </div>  
        </div>


        {databaseArray.map(renderRadData)}

        <Modal show={alertShow} onHide={handleAlertClose}>
                    <Modal.Header>
                        <Modal.Title>Create Task</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="light" onClick={handleAlertClose}>
                            Cancel
                        </Button> 
                        <Button variant="danger" onClick={() => {
                        }}>
                            Delete
                        </Button>     
                    </Modal.Footer>
                </Modal>


        </>
    )
}

export default Task;
