import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import "../style/Task.css" ;



import { addDoc, 
    setDoc, 
    collection, 
    onSnapshot, 
    query, 
    orderBy, 
    doc, 
    deleteDoc,
    getDoc,
    Timestamp } from 'firebase/firestore';
import db from "../database/firebase"




const Task = () => {

    const { projectId } = useParams();

    /* taskArray is [{"id": index, "data": { place:..., description:..., ... }}, 
                    {"id": index, "data": { place:..., description:..., ... }}, 
                    {"id": index, "data": { place:..., description:..., ... }},]
    */
    const [taskArray, setTaskArray] = useState([])

    /* projectData is [projectName: "Project 101",
                        description: "",
                        name: "",
                        date: "",
                        measureType: "", 
                        tasksData: []]
    */
    const [projectData, setProjectData] = useState([])

    useEffect(() => {
        const getData = async () => {
            const projData = await getDoc(doc(db, "Project", projectId))
            const dt = projData.data()
            setProjectData(dt)
            setTaskArray(dt.tasksData)
        }
        getData()
    }, [])  



    // Database to Card ----------------------------------------------

    const renderTaskData = (task, index) => {
        // task is {"id": index, "data": { place:..., description:..., ... }}
        const dateObj = new Date(task.data.date.seconds * 1000)
        const current = `${dateObj.getDate()}/${dateObj.getMonth()+1}/${dateObj.getFullYear()}`
        return(
            <div className='mca-display'>
                <div className='row container'>
                    <div className='col-12'>
                        <Card>
                            <Card.Body>
                                <Card.Title>{task.data.place}</Card.Title>
                                <Card.Text>{task.data.description}</Card.Text>
                                <Card.Text>{current}</Card.Text>
                            </Card.Body>
                            <Button variant="warning" className="m-1" onClick={() => {
                                setEditId(card.id)
                                setEditProjectName(card.data.projectName)
                                setEditDescription(card.data.description)
                                setEditName(card.data.name)
                                setEditMeasureType(card.data.measureType)
                                setEditDate(card.data.date)
                                handleEditShow()
                            }}>
                                <i className="fa-regular fa-pen-to-square"></i>
                            </Button>
                            <Button variant="danger" className="m-1" onClick={() => {       
                                setDeleteId(index)
                                handleAlertShow()
                            }}>
                                <i className="fa-regular fa-trash-can"></i>
                            </Button>
                            <Button variant="primary" className="m-1" onClick={() => {       
                                doTypeOperation(projectData.measureType)
                            }}>
                                {"Start Counting"}
                            </Button>
                        </Card>
                    </div>
                </div>  
            </div>
        )
    }


    // Type Operation ----------------------------------------------

    const doTypeOperation = () => {

    }



    // Add Task to Database ----------------------------------------------

    const [createShow, setCreateShow] = useState(false);
    const handleCreateClose = () => setCreateShow(false);
    const handleCreateShow = () => setCreateShow(true);

    const [data, setData] = useState({
        place: "Unknown Place",
        description: "This project does not have description",
        date: "",
        taskData: []
    })

    function onCreateDataChange(event) {
        const { name, value } = event.target
        setData((prevData) => {
            return {
                ...prevData,
                [name]: value
            }
        })
    }

    const formSubmit = async (event) => {
        event.preventDefault()

        const timeStamp = Timestamp.fromDate(new Date());
        setData((prevData) => {
            prevData["date"] = timeStamp
            return prevData
        })

        const index = taskArray.length + 1
        taskArray.push({"id": index, "data": data})

        await setDoc(doc(db, "Project", projectId), {
            projectName: projectData.projectName,
            description: projectData.description,
            name: projectData.name,
            date: projectData.date,
            measureType: projectData.measureType, 
            tasksData: taskArray
        })

        console.log("Creating Task: id = ", index)
        setData({
            place: "Unknown Place",
            description: "This task does not have description",
            date: "",
            taskData: []
        })
    }

    // Edit Task ----------------------------------------------


    // Delete Task ----------------------------------------------

    const [alertShow, setAlertShow] = useState(false);
    const handleAlertClose = () => setAlertShow(false);


    const handleAlertShow = () => setAlertShow(true);



    const [deleteId, setDeleteId] = useState("")


    return (
        <>
        <Navbar/>
        <div className='Task'>
            <div className='row container'>
                <div className='col-12'>
                    <Card>
                        <Card.Body>
                            <Card.Title>{projectData.projectName}</Card.Title>
                            <Card.Text>{projectData.name}</Card.Text>
                            <Card.Text>{"Time"}</Card.Text>
                            <Card.Text>{projectData.measureType}</Card.Text>
                        </Card.Body>
                    </Card>
                </div>
            </div>

            {taskArray.map(renderTaskData)}

            <div className='footer'>
                <Button variant="primary" className="NewProjectButton" onClick={handleCreateShow}>
                        <p><i class="fa-solid fa-plus"></i></p>
                </Button>
            </div>  
        </div>









        <Modal show={createShow} onHide={handleCreateClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Create Task</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form onSubmit={formSubmit} id="create-task">

                        <Form.Group className="mb-3" controlId="formPlace">
                            <Form.Label>Place</Form.Label>
                            <Form.Control 
                                name="place" 
                                placeholder="place" 
                                onChange={onCreateDataChange}
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control 
                                as="textarea" rows={3}
                                name="description" 
                                placeholder="description" 
                                onChange={onCreateDataChange}
                            />
                        </Form.Group>

                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={handleCreateClose} type="submit" form="create-task" value="Submit">Create Task</Button>     
                </Modal.Footer>
            </Modal>



            <Modal show={alertShow} onHide={handleAlertClose}>
                <Modal.Header>
                    <Modal.Title>Deleting Project ?</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <p>
                    When clicking on the "Delete" button, 
                    the system will remove this project from the database permanently. 
                    It means that data in project cannot be restored.
                </p>    
                <p>
                    Press "Cancel" button to cancel.
                </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="light" onClick={handleAlertClose}>
                        Cancel
                    </Button> 
                    <Button variant="danger" onClick={() => {
                         const deleteProject = async () => {
                             await deleteDoc(doc(db, "Project", deleteId));    
                         }  
                        deleteProject()
                        handleAlertClose()
                        console.log("Deleting Project: id = ",deleteId)
                    }}>
                        Delete
                    </Button>     
                </Modal.Footer>
            </Modal>












        </>
    )
}

export default Task;
